interface LessonData {
  missingSentenceOrder: number,
  courseNameEnglish: string,
  hebrewSentence: string,
  hebrewWord: string,
  englishSentence: string,
  englishWord: string,
  germanSentence: string,
  germanWord: string,
  frenchSentence: string,
  frenchWord: string,
  italianSentence: string,
  italianWord: string,
  spanishSentence: string,
  spanishWord: string,
}
export const generateLessonsWordsJson = (lessons: LessonData[]) => {
  const senteces: Array<{
    hebrew: string; 
    english: string; 
    german: string; 
    french: string;
    spanish: string;
  }> = [];

  for (const lesson of lessons) {
    senteces.push({
      hebrew: lesson.hebrewSentence,
      english: lesson.englishSentence,
      german: lesson.germanSentence,
      french: lesson.frenchSentence,
      spanish: lesson.spanishSentence,
    })
  }

  return senteces;
}

